            {/* Menu Sections - Main Content */}
            <div className="bg-white py-12 md:py-16">
                <div className="container mx-auto px-6 md:px-10 lg:px-16">
                    {/* Menu Content Container */}
                    <div className="relative overflow-hidden">
                        {/* Breakfast Section */}
